import java.nio.MappedByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;
import org.agrona.Arena;
import org.agrona.DirectBuffer;

public class MemorySegmentReaderSupplier implements ReaderSupplier {
    private final Arena arena;
    private final MemorySegment memory;

    public MemorySegmentReaderSupplier(Path path) throws IOException {
        try (FileChannel channel = FileChannel.open(path, StandardOpenOption.READ)) {
            long size = channel.size();
            MappedByteBuffer buffer = channel.map(FileChannel.MapMode.READ_ONLY, 0, size);
            this.arena = new Arena(size);
            this.memory = arena.allocateDirect((int) size);
            buffer.get(memory.array(), 0, (int) size);
        }
    }

    @Override
    public MemorySegmentReader get() {
        return new MemorySegmentReader(memory);
    }

    @Override
    public void close() {
        if (memory != null) {
            arena.free(memory);
        }
    }
}
