import java.io.*;
import java.net.*;
import java.util.*;

public class HttpClient {
    private String url;
    private HttpURLConnection con;
    private OutputStream os;
    private String delimiter = "--";
    private String boundary;
    private HttpConnectionRetryHandler handler;
    private String proxyHost;
    private String proxyPort;
    private static HttpClient me;

    private HttpClient() {
        boundary = UUID.randomUUID().toString();
    }

    public static HttpClient getInstance() {
        if (me == null) {
            me = new HttpClient();
        }
        return me;
    }

    public void connect(String method, Properties props) throws HttpClientException {
        try {
            doConnection(method, props);
        } catch (IOException e) {
            throw new HttpClientException("Failed to connect", e);
        }
    }

    private void doConnection(String method, Properties props) throws IOException {
        URL urlObj = new URL(this.url);
        con = (HttpURLConnection) urlObj.openConnection();
        if (proxyHost != null && proxyPort != null) {
            con.setProxy(new Proxy(Proxy.Type.HTTP, new InetSocketAddress(proxyHost, Integer.parseInt(proxyPort))));
        }
        con.setRequestMethod(method);
        for (Object key : props.keySet()) {
            con.setRequestProperty((String) key, (String) props.get(key));
        }
        con.setDoOutput(true);
        con.setDoInput(true);
    }

    public byte[] downloadImage(String imgName) throws IOException {
        URL urlObj = new URL(url + imgName);
        HttpURLConnection con = (HttpURLConnection) urlObj.openConnection();
        con.setRequestMethod("GET");
        con.setDoOutput(true);
        try (InputStream is = con.getInputStream()) {
            return readInputStream(is);
        } finally {
            con.disconnect();
        }
    }

    public void connectForMultipart() throws Exception {
        con.setRequestProperty("Content-Type", "multipart/form-data; boundary=" + boundary);
    }

    public void addFormPart(String paramName, String value) throws Exception {
        writeParamData(paramName, value);
    }

    public void addFilePart(String paramName, String fileName, byte[] data) throws Exception {
        String part = "--" + boundary + "\r\n";
        part += "Content-Disposition: form-data; name=\"" + paramName + "\"; filename=\"" + fileName + "\"\r\n";
        part += "Content-Type: application/octet-stream\r\n\r\n";
        os.write(part.getBytes());
        os.write(data);
        os.write("\r\n".getBytes());
    }

    public void finishMultipart() throws Exception {
        String part = "--" + boundary + "--\r\n";
        os.write(part.getBytes());
        os.flush();
    }

    public String getResponse() throws Exception {
        StringBuilder response = new StringBuilder();
        try (BufferedReader in = new BufferedReader(new InputStreamReader(con.getInputStream()))) {
            String inputLine;
            while ((inputLine = in.readLine()) != null) {
                response.append(inputLine);
            }
        }
        return response.toString();
    }

    private void writeParamData(String paramName, String value) throws Exception {
        String part = "--" + boundary + "\r\n";
        part += "Content-Disposition: form-data; name=\"" + paramName + "\"\r\n\r\n";
        part += value + "\r\n";
        os.write(part.getBytes());
    }

    public void setUrl(String url) {
        this.url = url;
    }

    public void setProxy(String host, String port) {
        this.proxyHost = host;
        this.proxyPort = port;
    }

    private byte[] readInputStream(InputStream is) throws IOException {
        ByteArrayOutputStream buffer = new ByteArrayOutputStream();
        int nRead;
        byte[] data = new byte[16384];
        while ((nRead = is.read(data, 0, data.length)) != -1) {
            buffer.write(data, 0, nRead);
        }
        buffer.flush();
        return buffer.toByteArray();
    }
}

class HttpClientException extends Exception {
    public HttpClientException(String message, Throwable cause) {
        super(message, cause);
    }
}

class HttpConnectionRetryHandler {
    public void handleRetry(HttpURLConnection con) {
        // Implement retry logic
    }
}
