            connection.setDoOutput(true);
            connection.getOutputStream().write(xmlStr.getBytes());
        }
        try (BufferedReader reader = new BufferedReader(new InputStreamReader(connection.getInputStream()))) {
            StringBuilder response = new StringBuilder();
            String line;
            while ((line = reader.readLine()) != null) {
                response.append(line);
            }
            DocumentBuilderFactory factory = DocumentBuilderFactory.newInstance();
            DocumentBuilder builder = factory.newDocumentBuilder();
            Document document = builder.parse(new java.io.ByteArrayInputStream(response.toString().getBytes()));
            parseDocument(document.getDocumentElement(), resultMap);
        } finally {
            connection.disconnect();
        }
        return resultMap;
    }

    private static void parseDocument(Node node, Map<String, Object> resultMap) {
        if (node.getNodeType() == Node.ELEMENT_NODE) {
            NodeList children = node.getChildNodes();
            for (int i = 0; i < children.getLength(); i++) {
                Node child = children.item(i);
                if (child.getNodeType() == Node.TEXT_NODE && child.getTextContent().trim().length() > 0) {
                    resultMap.put(node.getNodeName(), child.getTextContent());
                } else if (child.getNodeType() == Node.ELEMENT_NODE) {
                    parseDocument(child, resultMap);
                }
            }
        }
    }
}
