import java.io.IOException;
import java.net.HttpURLConnection;
import java.net.URL;
import java.nio.file.Path;
import java.nio.file.Paths;
import java.nio.file.StandardOpenOption;
import java.util.Optional;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

public class HttpDownloader {
    private static final int MAXIMUM_REDIRECTS = 8;
    private final Logger logger = LoggerFactory.getLogger(HttpDownloader.class);

    public void download(String targetUrlString, Path path, String userAgent) throws IOException {
        int redirects = 0;
        String urlString = targetUrlString;
        while (true) {
            Optional<String> nextUrl = getDownloadedOrNextUrl(urlString, path, userAgent);
            if (nextUrl.isPresent()) {
                urlString = nextUrl.get();
                redirects++;
                if (redirects > MAXIMUM_REDIRECTS) {
                    throw new IOException("Exceeded maximum number of redirects");
                }
            } else {
                break;
            }
        }
    }

    private Optional<String> getDownloadedOrNextUrl(String urlString, Path path, String userAgent) throws IOException {
        URL url = new URL(urlString);
        HttpURLConnection connection = (HttpURLConnection) url.openConnection();
        connection.setRequestProperty("User-Agent", userAgent);
        int responseCode = connection.getResponseCode();
        if (responseCode == HttpURLConnection.HTTP_MOVED_TEMP || responseCode == HttpURLConnection.HTTP_MOVED_PERM) {
            String newUrl = connection.getHeaderField("Location");
            connection.disconnect();
            return Optional.of(newUrl);
        } else if (responseCode == HttpURLConnection.HTTP_OK) {
            try (java.io.InputStream in = connection.getInputStream()) {
                byte[] buffer = new byte[4096];
                int bytesRead;
                try (java.nio.file.Files.newOutputStream(path, StandardOpenOption.CREATE, StandardOpenOption.WRITE)) {
                    while ((bytesRead = in.read(buffer)) != -1) {
                        java.nio.file.Files.write(path, buffer, 0, bytesRead);
                    }
                }
            }
            connection.disconnect();
            return Optional.empty();
        } else {
            connection.disconnect();
            throw new IOException("Failed to download file: HTTP error code " + responseCode);
        }
    }
}
