        this.lazy = lazy;
    }

    public int readInt(FileChannel channel) throws IOException {
        ByteBuffer buffer = ByteBuffer.allocate(4);
        channel.read(buffer);
        buffer.flip();
        return buffer.getInt();
    }

    public byte[] readBytes(FileChannel channel, int length) throws IOException {
        ByteBuffer buffer = ByteBuffer.allocate(length);
        channel.read(buffer);
        buffer.flip();
        return buffer.array();
    }

    public byte[] readBytes(FileChannel channel, long offset, int length) throws IOException {
        channel.position(offset);
        return readBytes(channel, length);
    }

    public BlobHeader readBlobHeader(FileChannel channel) throws IOException {
        BlobHeader header = new BlobHeader();
        header.setLength(readInt(channel));
        header.setVersion(readInt(channel));
        header.setTimestamp(readInt(channel));
        header.setChangeset(readInt(channel));
        header.setUserId(readInt(channel));
        header.setUserTimestamp(readInt(channel));
        header.setSequenceNumber(readInt(channel));
        return header;
    }

    public FileChannel openChannel() throws IOException {
        return FileChannel.open(path, StandardOpenOption.READ);
    }

    public class LazyReader implements OsmBlockSource {
        private FileChannel channel;
        private long position;
        private int blockSize;

        public LazyReader(FileChannel channel, long position, int blockSize) {
            this.channel = channel;
            this.position = position;
            this.blockSize = blockSize;
        }

        public void forEachBlock(Consumer<Block> consumer) throws IOException {
            while (position < channel.size()) {
                byte[] blockBytes = readBytes(channel, position, blockSize);
                Block block = new Block(blockBytes);
                consumer.accept(block);
                position += blockSize;
            }
        }

        public void close() throws IOException {
            channel.close();
        }

        public record LazyBlock(long offset, int length, FileChannel channel) {
            public Iterable<OsmElement> decodeElements() {
                return () -> new Iterator<>() {
                    private int index = 0;

                    @Override
                    public boolean hasNext() {
                        return index < length;
                    }

                    @Override
                    public OsmElement next() {
                        byte[] elementBytes = new byte[index + 1];
                        System.arraycopy(blockBytes, index, elementBytes, 0, elementBytes.length);
                        index += elementBytes.length;
                        return decodeElement(elementBytes);
                    }
                };
            }
        }
    }

    public interface OsmBlockSource {
        void forEachBlock(Consumer<Block> consumer) throws IOException;
    }

    public interface Block {
        Iterable<OsmElement> decodeElements();
    }

