public class ReadablePmtiles implements ReadableTileArchive {
    private SeekableByteChannel channel;
    private Pmtiles.Header header;

    public static ReadableTileArchive newReadFromFile(Path path) throws IOException {
        try (SeekableByteChannel channel = FileChannel.open(path, StandardOpenOption.READ)) {
            Pmtiles.Header header = Pmtiles.Header.readFrom(channel);
            return new ReadablePmtiles(channel, header);
        }
    }

    private ReadablePmtiles(SeekableByteChannel channel, Pmtiles.Header header) {
        this.channel = channel;
        this.header = header;
    }

    public byte[] getTile(int x, int y, int z) throws IOException {
        long tileOffset = header.getTileOffset(x, y, z);
        if (tileOffset == -1) {
            return null;
        }
        channel.position(tileOffset);
        ByteBuffer buffer = ByteBuffer.allocate(header.getTileSize(x, y, z));
        channel.read(buffer);
        buffer.flip();
        return buffer.array();
    }

    public void close() throws IOException {
        channel.close();
    }

    public Iterator<Tile> getAllTiles() throws IOException {
        return new Iterator<Tile>() {
            private final List<Pmtiles.Entry> dir = readDir(header.getDirectoryOffset(), header.getDirectorySize());

            @Override
            public boolean hasNext() {
                return !dir.isEmpty();
            }

            @Override
            public Tile next() {
                Pmtiles.Entry entry = dir.remove(0);
                try {
                    return new Tile(entry.getName(), getTile(entry.getX(), entry.getY(), entry.getZ()));
                } catch (IOException e) {
                    throw new RuntimeException(e);
                }
            }
        };
    }

    private byte[] getBytes(long start, int length) throws IOException {
        ByteBuffer buffer = ByteBuffer.allocate(length);
        channel.position(start);
        channel.read(buffer);
        buffer.flip();
        return buffer.array();
    }

    private List<Pmtiles.Entry> readDir(long offset, int length) throws IOException {
        channel.position(offset);
        ByteBuffer buffer = ByteBuffer.allocate(length);
        channel.read(buffer);
        buffer.flip();
        return Pmtiles.Entry.parseFrom(buffer);
    }

    private Stream<Tile> getTiles(List<Pmtiles.Entry> dir) {
        return dir.stream().map(entry -> {
            try {
                return new Tile(entry.getName(), getTile(entry.getX(), entry.getY(), entry.getZ()));
            } catch (IOException e) {
                throw new RuntimeException(e);
            }
        });
    }
}
