@Component
public class NettyClient {

    private static final Integer RECONNECT_SECONDS = 20;

    private Logger logger = LoggerFactory.getLogger(getClass());

    @Value("${netty.server.host}")
    private String serverHost;
    @Value("${netty.server.port}")
    private Integer serverPort;

    private EventLoopGroup eventGroup = new NioEventLoopGroup();
    private volatile Channel channel;

    @PostConstruct
    public void start() throws InterruptedException {
        Bootstrap bootstrap = new Bootstrap();
        bootstrap.group(eventGroup)
                .channel(NioSocketChannel.class)
                .remoteAddress(serverHost, serverPort)
                .option(ChannelOption.SO_KEEPALIVE, true)
                .option(ChannelOption.TCP_NODELAY, true)
                .handler(new NettyClientHandlerInitializer());

        bootstrap.connect().addListener(new ChannelFutureListener() {
            @Override
            public void operationComplete(ChannelFuture future) throws Exception {
                if (!future.isSuccess()) {
                    logger.error("[start][Netty Client connection to server({}:{}) failed]", serverHost, serverPort);
                    reconnect();
                    return;
                }
                channel = future.channel();
                logger.info("[start][Netty Client connected to server({}:{}) successfully]", serverHost, serverPort);
            }
        });
    }

    public void reconnect() {
        eventGroup.schedule(new Runnable() {
            @Override
            public void run() {
                logger.info("[reconnect][Starting reconnection]");
                try {
                    start();
                } catch (InterruptedException e) {
                    logger.error("[reconnect][Reconnection failed]", e);
                }
            }
        }, RECONNECT_SECONDS, TimeUnit.SECONDS);
        logger.info("[reconnect][Will attempt reconnection in {} seconds]", RECONNECT_SECONDS);
    }

    @PreDestroy
    public void shutdown() {
        if (channel != null) {
            channel.close();
        }
        eventGroup.shutdownGracefully();
    }

    public void send(Invocation invocation) {
        if (channel == null) {
            logger.error("[send][Connection does not exist]");
            return;
        }
        if (!channel.isActive()) {
            logger.error("[send][Connection({}) is not active]", channel.id());
            return;
        }
        channel.writeAndFlush(invocation);
    }
}

