import java.nio.channels.FileChannel;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;
import java.util.logging.Logger;

public abstract class AppendStoreMmap {
    protected DataOutputStream outputStream;
    protected int segmentBits;
    protected long segmentMask;
    protected long segmentBytes;
    protected Path path;
    protected boolean madvise;
    protected long outIdx;
    protected MappedByteBuffer[] segments;
    protected FileChannel channel;
    protected static final Logger LOGGER = Logger.getLogger(AppendStoreMmap.class.getName());

    public AppendStoreMmap(Path path, boolean madvise) {
        this(path, 1L << 30, madvise);
    }

    public AppendStoreMmap(Path path, long segmentSizeBytes, boolean madvise) {
        this.path = path;
        this.madvise = madvise;
        this.segmentBytes = segmentSizeBytes;
        this.segmentBits = 30;
        this.segmentMask = segmentBytes - 1;
        this.outIdx = 0;
        try {
            this.channel = FileChannel.open(path, StandardOpenOption.WRITE);
            this.segments = new MappedByteBuffer[1];
        } catch (IOException e) {
            LOGGER.severe("Failed to open file channel: " + e.getMessage());
        }
    }

    public MappedByteBuffer[] getSegments() {
        if (segments == null) {
            try {
                long fileSize = channel.size();
                long numSegments = (fileSize + segmentMask) >>> segmentBits;
                segments = new MappedByteBuffer[(int) numSegments];
                for (int i = 0; i < segments.length; i++) {
                    long pos = i << segmentBits;
                    segments[i] = channel.map(FileChannel.MapMode.READ_WRITE, pos, segmentBytes);
                    if (madvise) {
                        segments[i].getChannel().force(true);
                    }
                }
            } catch (IOException e) {
                LOGGER.severe("Failed to map segments: " + e.getMessage());
            }
        }
        return segments;
    }

    public void close() {
        if (segments != null) {
            for (MappedByteBuffer segment : segments) {
                if (segment != null) {
                    segment.force(true);
                    segment = null;
                }
            }
            segments = null;
        }
        if (channel != null) {
            try {
                channel.close();
            } catch (IOException e) {
                LOGGER.severe("Failed to close file channel: " + e.getMessage());
            }
        }
    }

    public long diskUsageBytes() {
        try {
            return channel.size();
        } catch (IOException e) {
            LOGGER.severe("Failed to get file size: " + e.getMessage());
            return -1;
        }
    }
