                 if (sslContext != null) {
                     p.addLast(sslContext.newHandler(ch.alloc()));
                 }
                 p.addLast(new HttpObjectAggregator(1048576));
                 p.addLast(communicationHandler);
             }
         });

        if (sslContext != null) {
