import io.netty.handler.codec.http.websocketx.WebSocketHandler;
import io.netty.handler.logging.LogLevel;
import io.netty.handler.logging.LoggingHandler;
import io.netty.handler.ssl.SslContext;
import io.netty.handler.ssl.SslContextBuilder;
import io.netty.handler.timeout.IdleStateHandler;
import io.netty.util.CharsetUtil;
import java.net.URI;
import java.util.concurrent.TimeUnit;
import javax.net.ssl.SSLException;

public class NettyTcpClient {
    private static final Logger LOG = LoggerFactory.getLogger(NettyTcpClient.class);
    private URI uri;
    private Channel webSocketChannel;
    private EventLoopGroup eventLoopGroup;
    private boolean compressedMessages;
    private boolean acceptAllCertificates;
    private boolean enableLoggingHandler;
    private LogLevel loggingHandlerLevel;
    private String socksProxyHost;
    private int socksProxyPort;

    public void connect() throws SSLException {
        eventLoopGroup = new NioEventLoopGroup();
        Bootstrap bootstrap = new Bootstrap();
        bootstrap.group(eventLoopGroup)
                .channel(NioSocketChannel.class)
                .handler(new ChannelInitializer<SocketChannel>() {
                    @Override
                    protected void initChannel(SocketChannel ch) {
                        ChannelPipeline pipeline = ch.pipeline();
                        if (enableLoggingHandler) {
                            pipeline.addLast(new LoggingHandler(loggingHandlerLevel));
                        }
                        if (acceptAllCertificates) {
                            SslContext sslContext = SslContextBuilder.forClient()
                                    .trustManager(InsecureTrustManagerFactory.INSTANCE)
                                    .build();
                            pipeline.addLast(sslContext.newHandler(ch.alloc()));
                        }
                        if (socksProxyHost != null && socksProxyPort != 0) {
                            pipeline.addLast(new Socks5ProxyHandler(socksProxyHost, socksProxyPort));
                        }
                        pipeline.addLast(new HttpRequestEncoder(), new HttpResponseDecoder(),
                                new HttpObjectAggregator(65536), new WebSocketClientHandshakerFactory(uri.getScheme(), uri.getPort(), uri.getHost(), null, null, true).newHandshaker(uri),
                                new WebSocketHandler() {
                                    @Override
                                    protected void handleWebSocketFrame(ChannelHandlerContext ctx, WebSocketFrame frame) {
                                        if (compressedMessages) {
                                            frame = new CompressedWebSocketFrame(frame);
                                        }
                                        ctx.fireChannelRead(frame.retain());
                                    }
                                });
                        }
                        pipeline.addLast(new IdleStateHandler(0, 0, 0, TimeUnit.SECONDS));
                    }
                });
        webSocketChannel = bootstrap.connect(uri.getHost(), uri.getPort()).sync().channel();
    }

    public void disconnect() {
        if (webSocketChannel != null) {
            webSocketChannel.close().sync();
        }
        eventLoopGroup.shutdownGracefully();
    }

    public WebSocketClientExtensionHandler getWebSocketClientExtensionHandler() {
        return new WebSocketClientCompressionHandler();
    }

    public WebSocketClientHandler getWebSocketClientHandler(WebSocketClientHandshaker handshaker, WebSocketClientHandler.WebSocketMessageHandler handler) {
        return new WebSocketClientHandler(handshaker, handler);
    }
}
