            SSLParameters sslParameters = enableHostNameVerification(dockerClientConfig.getSslParameters());
            return SslContextBuilder.forClient()
                    .trustManager(InsecureTrustManagerFactory.INSTANCE)
                    .sslParameters(sslParameters)
                    .build();
        } catch (Exception e) {
