    }

    private SSLParameters enableHostNameVerification(SSLParameters sslParameters) {
        sslParameters.setNeedClientAuth(true);
        sslParameters.setUseServerNameIndication(true);
        return sslParameters;
    }

    public void close() {
        eventLoopGroup.shutdownGracefully();
    }

    private class ReadTimeoutHandler extends ReadTimeoutHandler {
        public ReadTimeoutHandler(int readTimeout) {
            super(readTimeout);
        }
    }
