import io.netty.channel.EventLoopGroup;
import io.netty.channel.nio.NioEventLoopGroup;
import io.netty.channel.socket.SocketChannel;
import io.netty.channel.socket.nio.NioServerSocketChannel;
import io.netty.handler.ssl.SslContext;
import io.netty.handler.ssl.SslContextBuilder;
import io.netty.handler.ssl.util.SelfSignedCertificate;

import javax.net.ssl.SSLException;
import java.security.cert.CertificateException;
import java.security.cert.X509Certificate;

public class NettyServer {
    private final Blade blade;
    private final Environment environment;
    private final EventLoopGroup bossGroup;
    private final EventLoopGroup workerGroup;
    private final Channel channel;
    private static final int DEFAULT_ACCEPT_THREAD_COUNT = 2;
    private static final int DEFAULT_IO_THREAD_COUNT = 4;

    public NettyServer(Blade blade, Environment environment) {
        this.blade = blade;
        this.environment = environment;
        this.bossGroup = new NioEventLoopGroup(DEFAULT_ACCEPT_THREAD_COUNT);
        this.workerGroup = new NioEventLoopGroup(DEFAULT_IO_THREAD_COUNT);
        this.channel = null;
    }

    public void startServer() throws InterruptedException, SSLException {
        final SslContext sslContext = configureSsl();
        ServerBootstrap b = new ServerBootstrap();
        b.group(bossGroup, workerGroup)
         .channel(NioServerSocketChannel.class)
         .childHandler(new ChannelInitializer<SocketChannel>() {
             @Override
             protected void initChannel(SocketChannel ch) throws Exception {
                 ch.pipeline().addLast(sslContext.newHandler(ch.alloc()));
                 // Additional handlers can be added here
             }
         })
         .option(ChannelOption.SO_BACKLOG, 128)
         .childOption(ChannelOption.SO_KEEPALIVE, true);

        int port = environment.getServerPort();
        ChannelFuture f = b.bind(port).sync();
        channel = f.channel();
        f.channel().closeFuture().sync();
    }

    private SslContext configureSsl() throws SSLException {
        if (environment.isSslEnabled()) {
            SelfSignedCertificate ssc = new SelfSignedCertificate();
            return SslContextBuilder.forServer(ssc.certificate(), ssc.privateKey()).build();
        }
        return null;
    }

    public void stop() throws InterruptedException {
        if (channel != null) {
            channel.close().sync();
        }
        workerGroup.shutdownGracefully();
        bossGroup.shutdownGracefully();
    }
}
