import java.nio.ByteBuffer;
import java.nio.channels.AsynchronousFileChannel;
import java.nio.channels.CompletionHandler;
import java.nio.file.OpenOption;
import java.nio.file.Path;
import java.util.concurrent.Future;
import java.util.concurrent.atomic.AtomicBoolean;
import io.vertx.core.AbstractVerticle;
import io.vertx.core.ContextInternal;
import io.vertx.core.Handler;
import io.vertx.core.VertxInternal;
import io.vertx.core.buffer.Buffer;
import io.vertx.core.impl.ContextInternal;
import io.vertx.core.impl.FutureImpl;
import io.vertx.core.impl.VertxInternal;

public class AsyncFileImpl extends AbstractVerticle implements AsyncFile {

    private VertxInternal vertx;
    private AsynchronousFileChannel ch;
    private ContextInternal context;
    private AtomicBoolean closed;
    private long writePos;
    private int readBufferSize;
    private Handler<Buffer> handler;
    private Handler<Void> endHandler;
    private long readPos;
    private long readLength;

    public AsyncFileImpl(VertxInternal vertx, String path, OpenOptions options, ContextInternal context) {
        this.vertx = vertx;
        this.context = context;
        this.closed = new AtomicBoolean(false);
        this.readBufferSize = 8192;
        this.readLength = Long.MAX_VALUE;
        try {
            this.ch = AsynchronousFileChannel.open(Path.of(path), options);
        } catch (Exception e) {
            throw new RuntimeException(e);
        }
    }

    @Override
    public Future<Void> close() {
        if (closed.compareAndSet(false, true)) {
            return new FutureImpl<Void>(context);
        }
        return new FutureImpl<Void>(context);
    }

    @Override
    public Future<Buffer> read(Buffer buffer, int offset, long position, int length) {
        ByteBuffer bb = buffer.getBinaryBuffer();
        return new FutureImpl<Buffer>(context, () -> {
            doRead(buffer, offset, bb, position, length);
            return buffer;
        });
    }

    @Override
    public Future<Void> write(Buffer buffer, long position) {
        ByteBuffer bb = buffer.getBinaryBuffer();
        return new FutureImpl<Void>(context, () -> {
            doWrite(bb, position);
            return null;
        });
    }

    private void doWrite(ByteBuffer buffer, long position) {
        doWrite(new ByteBuffer[]{buffer}, position, null, null);
    }

    private void doWrite(ByteBuffer[] buffers, long position, Handler<AsyncResult<Void>> handler) {
        doWrite(buffers, position, Long.MAX_VALUE, handler);
    }

    private void doWrite(ByteBuffer buffer, long position, long toWrite, Handler<AsyncResult<Void>> handler) {
        writeInternal(buffer, position, handler);
    }

    private void writeInternal(ByteBuffer buff, long position, Handler<AsyncResult<Void>> handler) {
        ch.write(buff, position, null, new CompletionHandler<Void, Void>() {
            @Override
            public void completed(Void result, Void attachment) {
                if (handler != null) {
                    handler.handle(Future.succeededFuture());
                }
            }

            @Override
            public void failed(Throwable exc, Void attachment) {
                if (handler != null) {
                    handler.handle(Future.failedFuture(exc));
                }
            }
        });
    }

    private void doRead(Buffer writeBuff, int offset, ByteBuffer buff, long position, Promise<Buffer> promise) {
        ch.read(buff, position, null, new CompletionHandler<Integer, Void>() {
            @Override
            public void completed(Integer result, Void attachment) {
                if (handler != null) {
                    handler.handle(writeBuff);
                }
                if (endHandler != null) {
                    endHandler.handle(null);
                }
                promise.complete(writeBuff);
            }

            @Override
            public void failed(Throwable exc, Void attachment) {
                promise.fail(exc);
            }
        });
    }

    private void check() {
        if (closed.get()) {
            throw new IllegalStateException("File is closed");
        }
    }

    private void closeInternal(Promise<Void> handler) {
        ch.close().thenAccept(v -> handler.complete());
    }
}
