import io.netty.handler.codec.string.StringEncoder;
import io.netty.handler.ssl.SslContext;
import io.netty.handler.ssl.SslContextBuilder;
import io.netty.handler.ssl.util.SelfSignedCertificate;

public class EchoServer {
    private boolean ssl;
    private int port;

    public EchoServer() {
        ssl = Boolean.parseBoolean(System.getProperty("ssl", "false"));
        port = Integer.parseInt(System.getProperty("port", "8007"));
    }

    public void start() throws Exception {
        EventLoopGroup bossGroup = new NioEventLoopGroup(1);
        EventLoopGroup workerGroup = new NioEventLoopGroup();
        try {
            SslContext sslContext = null;
            if (ssl) {
                SelfSignedCertificate ssc = new SelfSignedCertificate();
                sslContext = SslContextBuilder.forServer(ssc.certificate(), ssc.privateKey()).build();
            }

            ServerBootstrap b = new ServerBootstrap();
            b.group(bossGroup, workerGroup)
             .channel(NioServerSocketChannel.class)
             .option(ChannelOption.SO_BACKLOG, 100)
             .childHandler(new ChannelInitializer<SocketChannel>() {
                 @Override
                 protected void initChannel(SocketChannel ch) throws Exception {
                     ch.pipeline().addLast(new StringDecoder(), new StringEncoder(), new EchoServerHandler(sslContext));
                 }
             });

            ChannelFuture f = b.bind(port).sync();
            f.channel().closeFuture().sync();
        } finally {
            workerGroup.shutdownGracefully();
            bossGroup.shutdownGracefully();
        }
    }

    private static class EchoServerHandler extends io.netty.channel.SimpleChannelInboundHandler<String> {
        private final SslContext sslContext;

        public EchoServerHandler(SslContext sslContext) {
            this.sslContext = sslContext;
        }

        @Override
        protected void channelRead0(io.netty.channel.ChannelHandlerContext ctx, String msg) throws Exception {
            if (sslContext != null) {
                ctx.writeAndFlush(sslContext.newHandler(ctx.alloc()).wrap(msg));
            } else {
                ctx.writeAndFlush(msg);
            }
        }

        @Override
        public void exceptionCaught(io.netty.channel.ChannelHandlerContext ctx, Throwable cause) throws Exception {
            cause.printStackTrace();
            ctx.close();
        }
    }

    public static void main(String[] args) {
        try {
            new EchoServer().start();
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
}
