import io.netty.channel.socket.nio.NioServerSocketChannel;
import io.netty.handler.logging.LogLevel;
import io.netty.handler.logging.LoggingHandler;
import io.netty.handler.ssl.SslContext;
import io.netty.handler.ssl.SslContextBuilder;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

public abstract class AbstractHttp2ServerTransport {
    private static final Logger LOGGER = LoggerFactory.getLogger(AbstractHttp2ServerTransport.class);
    private final ServerBootstrap serverBootstrap;
    private final EventLoopGroup bizGroup;
    private final ServerTransportConfig transportConfig;

    public AbstractHttp2ServerTransport(ServerTransportConfig transportConfig) {
        this.transportConfig = transportConfig;
        this.serverBootstrap = new ServerBootstrap();
        this.bizGroup = new NioEventLoopGroup();
    }

    public boolean start() {
        try {
            serverBootstrap.group(bizGroup, bizGroup)
                    .channel(NioServerSocketChannel.class)
                    .handler(new LoggingHandler(LogLevel.INFO))
                    .childHandler(new ChannelInitializer<SocketChannel>() {
                        @Override
                        protected void initChannel(SocketChannel ch) throws Exception {
                            ChannelPipeline pipeline = ch.pipeline();
                            // Add SSL support if required
                            if (transportConfig.isSslEnabled()) {
                                SslContext sslContext = SslContextBuilder.forServer(transportConfig.getSslKey(), transportConfig.getSslCert()).build();
                                pipeline.addLast(sslContext.newHandler(ch.alloc()));
                            }
                            // Add HTTP/2 specific handlers
                            pipeline.addLast(new Http2ServerInitializer());
                        }
                    })
                    .option(ChannelOption.SO_BACKLOG, transportConfig.getBacklog())
                    .childOption(ChannelOption.SO_KEEPALIVE, transportConfig.isKeepAlive());

            ChannelFuture future = serverBootstrap.bind(transportConfig.getHost(), transportConfig.getPort()).sync();
            LOGGER.info("Server started on {}:{}", transportConfig.getHost(), transportConfig.getPort());
            return future.isSuccess();
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
            LOGGER.error("Server start interrupted", e);
            return false;
        }
    }

    public void stop() {
        try {
            serverBootstrap.group(bizGroup).shutdownGracefully().sync();
            LOGGER.info("Server stopped");
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
            LOGGER.error("Server stop interrupted", e);
        }
    }
}
