import java.io.File;
import java.io.IOException;
import java.nio.MappedByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.StandardOpenOption;

public class FileChannelInteractor {
    private FileChannel fileChannel;
    private long mapAddress;
    private long fileSize;

    public FileChannelInteractor(String filePath) throws IOException {
        fileChannel = FileChannel.open(new File(filePath).toPath(), StandardOpenOption.READ);
        MappedByteBuffer buffer = fileChannel.map(FileChannel.MapMode.READ_ONLY, 0, fileChannel.size());
        mapAddress = buffer.address();
        fileSize = buffer.limit();
    }

    public long getMemoryMappedAddress() {
        return mapAddress;
    }

    public long getFileSize() {
        return fileSize;
    }

    public void close() throws IOException {
        if (fileChannel != null) {
            fileChannel.close();
        }
    }

    public static void spawnWorkerProcess() {
        String[] args = new String[Runtime.getRuntime().exec("ps -ef | grep java").getInputStream().available() + 1];
        args[0] = "java";
        for (int i = 1; i < args.length; i++) {
            args[i] = "--worker";
        }
        try {
            Runtime.getRuntime().exec(args);
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
}
