import io.netty.bootstrap.ServerBootstrap;
import io.netty.channel.*;
import io.netty.channel.nio.NioEventLoopGroup;
import io.netty.channel.socket.SocketChannel;
import io.netty.channel.socket.nio.NioServerSocketChannel;
import io.netty.handler.codec.string.StringDecoder;
import io.netty.handler.codec.string.StringEncoder;

import java.util.HashMap;
import java.util.Map;

public class Console {
    private static Console instance;
    private Map<String, Class> commands;

    private Console() {
        commands = new HashMap<>();
        commands.put("help", HelpCommand.class);
        commands.put("hello", HelloCommand.class);
        commands.put("device", DeviceCommand.class);
    }

    public static synchronized Console getInstance() {
        if (instance == null) {
            instance = new Console();
        }
        return instance;
    }

    public void listenOnTCP(int port) {
        EventLoopGroup bossGroup = new NioEventLoopGroup();
        EventLoopGroup workerGroup = new NioEventLoopGroup();
        try {
            ServerBootstrap b = new ServerBootstrap();
            b.group(bossGroup, workerGroup)
             .channel(NioServerSocketChannel.class)
             .childHandler(new ChannelInitializer<SocketChannel>() {
                 @Override
                 protected void initChannel(SocketChannel ch) throws Exception {
                     ch.pipeline().addLast(new StringDecoder(), new StringEncoder(), new Adapter());
                 }
             });

            ChannelFuture f = b.bind(port).sync();
            f.channel().closeFuture().sync();
        } catch (InterruptedException e) {
            e.printStackTrace();
        } finally {
            workerGroup.shutdownGracefully();
            bossGroup.shutdownGracefully();
        }
    }

    class Adapter extends ChannelInboundHandlerAdapter {
        @Override
        public void channelRead(ChannelHandlerContext ctx, Object msg) throws Exception {
            String command = (String) msg;
            Class<?> commandClass = commands.get(command);
            if (commandClass != null) {
                Command commandInstance = (Command) commandClass.getDeclaredConstructor(String.class).newInstance(command);
                ctx.writeAndFlush(commandInstance.execute() + "\n");
            } else {
                ctx.writeAndFlush("Unknown command\n");
            }
        }

        @Override
        public void channelActive(ChannelHandlerContext ctx) throws Exception {
            ctx.writeAndFlush("Connected to Console Server\n");
            sendPrompt(ctx);
        }

        @Override
        public void channelReadComplete(ChannelHandlerContext ctx) throws Exception {
            ctx.flush();
        }

        private void sendStringL(ChannelHandlerContext ctx, String text) {
            ctx.writeAndFlush(text + "\n");
        }

        private void sendString(ChannelHandlerContext ctx, String text) {
            ctx.writeAndFlush(text);
        }

        private void sendPrompt(ChannelHandlerContext ctx) {
            sendStringL(ctx, "Console> ");
        }
    }

    abstract static class Command {
        private String command;

        public Command(String command) {
            this.command = command;
        }

        public abstract String execute();
    }

    static class HelpCommand extends Command {
        public HelpCommand(String command) {
            super(command);
        }

        @Override
        public String execute() {
            return "Available commands: help, hello, device";
        }
    }

    static class HelloCommand extends Command {
        public HelloCommand(String command) {
            super(command);
        }

        @Override
        public String execute() {
            return "Hello, World!";
        }
    }

    static class DeviceCommand extends Command {
        public DeviceCommand(String command) {
            super(command);
        }

        @Override
        public String execute() {
            return "{\"device\":\"Console Server\"}";
        }
    }
}
