import io.netty.bootstrap.ServerBootstrap;
import io.netty.channel.*;
import io.netty.channel.nio.NioEventLoopGroup;
import io.netty.channel.socket.SocketChannel;
import io.netty.channel.socket.nio.NioServerSocketChannel;
import io.netty.handler.codec.string.StringDecoder;
import io.netty.handler.codec.string.StringEncoder;
import io.netty.util.concurrent.DefaultEventExecutorGroup;
import io.netty.util.concurrent.EventExecutorGroup;

import java.net.InetSocketAddress;
import java.util.concurrent.atomic.AtomicInteger;

public class NettyServer extends AbstractServer {
    private final NioEventLoopGroup bossGroup;
    private final NioEventLoopGroup workerGroup;
    private final Channel serverChannel;
    private final MessageHandler messageHandler;
    private final StandardThreadExecutor standardThreadExecutor;
    private final AtomicInteger rejectCounter;
    private final NettyServerChannelManage channelManage;

    public NettyServer(URL url, MessageHandler messageHandler) {
        this.bossGroup = new NioEventLoopGroup();
        this.workerGroup = new NioEventLoopGroup();
        this.serverChannel = null;
        this.messageHandler = messageHandler;
        this.standardThreadExecutor = new StandardThreadExecutor();
        this.rejectCounter = new AtomicInteger(0);
        this.channelManage = new NettyServerChannelManage();
    }

    @Override
    public boolean open() {
        try {
            ServerBootstrap b = new ServerBootstrap();
            b.group(bossGroup, workerGroup)
             .channel(NioServerSocketChannel.class)
             .childHandler(new ChannelInitializer<SocketChannel>() {
                 @Override
                 protected void initChannel(SocketChannel ch) throws Exception {
                     ChannelPipeline p = ch.pipeline();
                     p.addLast(new StringDecoder());
                     p.addLast(new StringEncoder());
                     p.addLast(standardThreadExecutor.getExecutor(), messageHandler);
                 }
             })
             .option(ChannelOption.SO_BACKLOG, 128)
             .childOption(ChannelOption.SO_KEEPALIVE, true);

            InetSocketAddress address = new InetSocketAddress(url.getPort());
            serverChannel = b.bind(address).sync().channel();
            return true;
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
            return false;
        }
    }

    @Override
    public void close() {
        if (serverChannel != null) {
            serverChannel.close().syncUninterruptibly();
        }
        workerGroup.shutdownGracefully();
        bossGroup.shutdownGracefully();
        standardThreadExecutor.shutdown();
    }

    @Override
    public boolean isBound() {
        return serverChannel != null && serverChannel.isActive();
    }

    @Override
    public Response request(Request request) throws MotanFrameworkException {
        throw new MotanFrameworkException("Server doesn't support direct requests");
    }

    public void setMessageHandler(MessageHandler messageHandler) {
        this.messageHandler = messageHandler;
    }

    public void setStandardThreadExecutor(StandardThreadExecutor standardThreadExecutor) {
        this.standardThreadExecutor = standardThreadExecutor;
    }
}
