import io.netty.handler.codec.http.HttpServerCodec;
import io.netty.handler.codec.http.HttpServerHandler;
import io.netty.handler.stream.ChunkedWriteHandler;
import io.netty.util.concurrent.DefaultEventExecutorGroup;
import io.netty.util.concurrent.EventExecutorGroup;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;
import java.util.concurrent.TimeUnit;
import java.util.function.Consumer;

public class NettySourceHttpServer {
    private EventLoopGroup bossGroup;
    private EventLoopGroup workerGroup;
    private Runnable nettyServerRunnable;
    private boolean isInitialized;
    private boolean isStarted;

    public NettySourceHttpServer(Context context, int threadCount) {
        bossGroup = new NioEventLoopGroup(threadCount);
        workerGroup = new NioEventLoopGroup();
        nettyServerRunnable = this::startServer;
        isInitialized = false;
        isStarted = false;
    }

    public void init(QueryRegistry queryRegistry, Subject<String, String> eventSubject, int port) {
        if (!isInitialized) {
            ServerBootstrap b = new ServerBootstrap();
            b.group(bossGroup, workerGroup)
             .channel(NioServerSocketChannel.class)
             .childHandler(new ChannelInitializer<SocketChannel>() {
                 @Override
                 protected void initChannel(SocketChannel ch) throws Exception {
                     ChannelPipeline p = ch.pipeline();
                     p.addLast(new HttpServerCodec());
                     p.addLast(new HttpObjectAggregator(65536));
                     p.addLast(new ChunkedWriteHandler());
                     p.addLast(new HttpServerHandler(queryRegistry, eventSubject));
                 }
             });
            try {
                ChannelFuture f = b.bind(port).sync();
                isInitialized = true;
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
            }
        }
    }

    public void startServer() {
        if (!isInitialized || isStarted) {
            return;
        }
        new Thread(nettyServerRunnable).start();
        isStarted = true;
    }

    public void shutdownServer() {
        if (isInitialized && isStarted) {
            bossGroup.shutdownGracefully();
            workerGroup.shutdownGracefully();
            isStarted = false;
        }
    }
}
