public class NettyRemotingServer {
    private final ServerBootstrap serverBootstrap;
    private final EventLoopGroup bossSelectorGroup;
    private final EventLoopGroup workerSelectorGroup;
    private final DefaultEventExecutorGroup defaultEventExecutorGroup;
    private final AppContext appContext;
    private final Logger LOGGER;

    public NettyRemotingServer(AppContext appContext, RemotingServerConfig config) {
        this.appContext = appContext;
        this.serverBootstrap = new ServerBootstrap();
        this.bossSelectorGroup = new NioEventLoopGroup();
        this.workerSelectorGroup = new NioEventLoopGroup();
        this.defaultEventExecutorGroup = new DefaultEventExecutorGroup(config.getWorkerThreads());
        this.LOGGER = LoggerFactory.getLogger(NettyRemotingServer.class);
    }

    public NettyRemotingServer(RemotingServerConfig config, ChannelEventListener listener) {
        this.appContext = null;
        this.serverBootstrap = new ServerBootstrap();
        this.bossSelectorGroup = new NioEventLoopGroup();
        this.workerSelectorGroup = new NioEventLoopGroup();
        this.defaultEventExecutorGroup = new DefaultEventExecutorGroup(config.getWorkerThreads());
        this.LOGGER = LoggerFactory.getLogger(NettyRemotingServer.class);
    }

    public void serverStart() throws InterruptedException {
        serverBootstrap.group(bossSelectorGroup, workerSelectorGroup)
                .channel(NioServerSocketChannel.class)
                .childHandler(new ChannelInitializer<SocketChannel>() {
                    @Override
                    protected void initChannel(SocketChannel ch) throws Exception {
                        ChannelPipeline pipeline = ch.pipeline();
                        pipeline.addLast(new IdleStateHandler(config.getIdleTimeSeconds(), 0, 0));
                        pipeline.addLast(defaultEventExecutorGroup, new NettyServerHandler());
                        pipeline.addLast(new NettyConnectManageHandler(listener));
                    }
                });
        ChannelFuture future = serverBootstrap.bind(config.getPort()).sync();
        LOGGER.info("Server started on port {}", config.getPort());
        future.channel().closeFuture().sync();
    }

    public void serverShutdown() throws InterruptedException {
        workerSelectorGroup.shutdownGracefully();
        bossSelectorGroup.shutdownGracefully();
        defaultEventExecutorGroup.shutdownGracefully();
        LOGGER.info("Server shutdown gracefully");
    }

    private class NettyServerHandler extends ChannelInboundHandlerAdapter {
        @Override
        public void channelRead0(ChannelHandlerContext ctx, RemotingCommand msg) throws Exception {
            // Handle incoming message
        }
    }

    private class NettyConnectManageHandler extends ChannelInboundHandlerAdapter {
        private final ChannelEventListener listener;

        public NettyConnectManageHandler(ChannelEventListener listener) {
            this.listener = listener;
        }

        @Override
        public void channelRegistered(ChannelHandlerContext ctx) throws Exception {
            listener.onChannelRegistered(ctx);
        }

        @Override
        public void channelUnregistered(ChannelHandlerContext ctx) throws Exception {
            listener.onChannelUnregistered(ctx);
        }

        @Override
        public void channelActive(ChannelHandlerContext ctx) throws Exception {
            listener.onChannelActive(ctx);
        }

        @Override
        public void channelInactive(ChannelHandlerContext ctx) throws Exception {
            listener.onChannelInactive(ctx);
        }

        @Override
        public void userEventTriggered(ChannelHandlerContext ctx, Object evt) throws Exception {
            if (evt instanceof IdleStateEvent) {
                IdleStateEvent event = (IdleStateEvent) evt;
                if (event.state() == IdleState.READER_IDLE || event.state() == IdleState.WRITER_IDLE) {
                    listener.onIdleState(ctx, event.state());
                }
            }
        }

        @Override
        public void exceptionCaught(ChannelHandlerContext ctx, Throwable cause) throws Exception {
            listener.onExceptionCaught(ctx, cause);
        }
    }
}
