                        pipeline.addLast(new ProxyMessageEncoder());
                        pipeline.addLast(new IdleCheckHandler(IdleCheckHandler.READ_IDLE_TIME, 
                                IdleCheckHandler.WRITE_IDLE_TIME, 0));
                        pipeline.addLast(new ServerChannelHandler());
