import java.net.http.HttpRequest.BodyPublishers;
import java.net.http.HttpResponse.BodyHandlers;
import java.util.Optional;
import com.fasterxml.jackson.databind.ObjectMapper;

public class BeaconClient {
    private int port;
    private String token;
    private HttpClient client;

    public BeaconClient(int port, String token) {
        this.port = port;
        this.token = token;
        this.client = HttpClient.newHttpClient();
    }

    public <RES> RES performRequest(BeaconInterface<?> prov, String rawNode) throws Exception {
        HttpRequest request = HttpRequest.newBuilder()
                .uri(URI.create("http://localhost:" + port + rawNode))
                .header("Authorization", "Bearer " + token)
                .POST(BodyPublishers.ofString(prov.toJson()))
                .build();

        HttpResponse<String> response = client.send(request, BodyHandlers.ofString());
        if (response.statusCode() >= 400 && response.statusCode() < 500) {
            return parseClientError(response).orElseThrow(() -> new Exception("Client error parsing failed"));
        } else if (response.statusCode() >= 500) {
            return parseServerError(response).orElseThrow(() -> new Exception("Server error parsing failed"));
        }

        ObjectMapper mapper = new ObjectMapper();
        return mapper.readValue(response.body(), prov.getClass());
    }

    public <REQ, RES> RES performRequest(REQ req) throws Exception {
        ObjectMapper mapper = new ObjectMapper();
        String json = mapper.writeValueAsString(req);
        HttpRequest request = HttpRequest.newBuilder()
                .uri(URI.create("http://localhost:" + port + req.getEndpoint()))
                .header("Authorization", "Bearer " + token)
                .POST(BodyPublishers.ofString(json))
                .build();

        HttpResponse<String> response = client.send(request, BodyHandlers.ofString());
        if (response.statusCode() >= 400 && response.statusCode() < 500) {
            return parseClientError(response).orElseThrow(() -> new Exception("Client error parsing failed"));
        } else if (response.statusCode() >= 500) {
            return parseServerError(response).orElseThrow(() -> new Exception("Server error parsing failed"));
        }

        return mapper.readValue(response.body(), req.getResponseType());
    }

    private <T> Optional<T> parseClientError(HttpResponse<String> response) throws Exception {
        ObjectMapper mapper = new ObjectMapper();
        BeaconClientErrorResponse error = mapper.readValue(response.body(), BeaconClientErrorResponse.class);
        return Optional.of(error);
    }

    private <T> Optional<T> parseServerError(HttpResponse<String> response) throws Exception {
        ObjectMapper mapper = new ObjectMapper();
        BeaconServerErrorResponse error = mapper.readValue(response.body(), BeaconServerErrorResponse.class);
        return Optional.of(error);
    }
}
