        HttpRequest.Builder requestBuilder = HttpRequest.newBuilder()
                .uri(URI.create(url))
                .header("Content-Type", convertParametersToJSON ? "application/json" : "application/x-www-form-urlencoded")
                .POST(convertParametersToJSON ? BodyPublishers.ofString(new ObjectMapper().writeValueAsString(parameters)) : BodyPublishers.ofString(buildFormDataFromMap(parameters)));

        for (Map.Entry<String, String> header : headers.entrySet()) {
            requestBuilder.header(header.getKey(), header.getValue());
        }

        return httpClient.send(requestBuilder.build(), BodyHandlers.ofString());
    }

    private HttpRequest.BodyPublisher buildFormDataFromMap(Map<Object, Object> data) {
        StringBuilder formData = new StringBuilder();
        for (Map.Entry<Object, Object> entry : data.entrySet()) {
            if (formData.length() > 0) {
                formData.append("&");
            }
            formData.append(entry.getKey().toString());
            formData.append("=");
            formData.append(entry.getValue().toString());
        }
        return BodyPublishers.ofString(formData.toString());
    }

    public JsonNode makeJsonRequest(String url) throws IOException, InterruptedException {
        HttpRequest request = HttpRequest.newBuilder()
                .uri(URI.create(url))
                .build();

