        FormBody.Builder formBuilder = new FormBody.Builder();
        if (params != null) {
            for (Map.Entry<String, String> entry : params.entrySet()) {
                formBuilder.add(entry.getKey(), entry.getValue());
            }
        }
        RequestBody body = formBuilder.build();
        Request request = new Request.Builder()
                .url(url)
                .post(body)
                .build();
        return executeRequest(request, 3);
    }

    private static Resp executeRequest(Request request, int retryTime) throws IOException {
        while (retryTime > 0) {
            try (Response response = client.newCall(request).execute()) {
                return new Resp.Builder()
                        .code(response.code())
                        .body(response.body().string())
                        .build();
            } catch (IOException e) {
                retryTime--;
                if (retryTime <= 0) {
                    throw e;
                }
            }
        }
        return new Resp.Builder().code(500).message("Request failed after retries").build();
    }

    public static String bodyToString(ResponseBody body) {
        try {
            return body.string();
        } catch (IOException e) {
            return "";
        }
    }

