import java.util.Collections;
import java.util.Iterator;
import java.util.List;
import java.util.Map;
import java.util.Properties;
import java.util.concurrent.TimeUnit;

public class KafkaItemReader<K, V> implements ItemReader<V> {

    private static final String TOPIC_PARTITION_OFFSETS = "topicPartitionOffsets";
    private static final long DEFAULT_POLL_TIMEOUT = TimeUnit.SECONDS.toMillis(30);

    private List<TopicPartition> topicPartitions;
    private Map<TopicPartition, Long> partitionOffsets;
    private KafkaConsumer<K, V> kafkaConsumer;
    private Properties consumerProperties;
    private Iterator<ConsumerRecord<K, V>> consumerRecords;
    private Duration pollTimeout;
    private boolean saveState;

    public KafkaItemReader(Properties properties, String topic, Integer... partitions) {
        this.consumerProperties = properties;
        this.topicPartitions = Collections.singletonList(new TopicPartition(topic, partitions[0]));
        this.partitionOffsets = new HashMap<>();
        this.pollTimeout = Duration.ofMillis(DEFAULT_POLL_TIMEOUT);
        this.saveState = true;
    }

    public KafkaItemReader(Properties properties, String topic, List<Integer> partitions) {
        this.consumerProperties = properties;
        this.topicPartitions = partitions.stream().map(partition -> new TopicPartition(topic, partition)).collect(Collectors.toList());
        this.partitionOffsets = new HashMap<>();
        this.pollTimeout = Duration.ofMillis(DEFAULT_POLL_TIMEOUT);
        this.saveState = true;
    }

    @Override
    public void open(ExecutionContext executionContext) throws Exception {
        this.kafkaConsumer = new KafkaConsumer<>(consumerProperties);
        if (executionContext.containsKey(TOPIC_PARTITION_OFFSETS)) {
            Map<TopicPartition, Long> storedOffsets = (Map<TopicPartition, Long>) executionContext.get(TOPIC_PARTITION_OFFSETS);
            partitionOffsets.putAll(storedOffsets);
        }
        kafkaConsumer.assign(topicPartitions);
        for (TopicPartition partition : topicPartitions) {
            if (partitionOffsets.containsKey(partition)) {
                kafkaConsumer.seek(partition, partitionOffsets.get(partition));
            } else {
                kafkaConsumer.seek(partition, 0);
            }
        }
        consumerRecords = kafkaConsumer.poll(pollTimeout);
    }

    @Override
    public V read() throws Exception {
        if (!consumerRecords.hasNext()) {
            return null;
        }
        ConsumerRecord<K, V> record = consumerRecords.next();
        partitionOffsets.put(record.topicPartition(), record.offset() + 1);
        return record.value();
    }

    @Override
    public void update(ExecutionContext executionContext) throws Exception {
        if (saveState) {
            executionContext.put(TOPIC_PARTITION_OFFSETS, partitionOffsets);
        }
        kafkaConsumer.commitSync();
    }

    @Override
    public void close() throws Exception {
        if (kafkaConsumer != null) {
            kafkaConsumer.close();
        }
    }
}
