    private Condition offsetReachedThreshold;
    private Consumer<byte[], byte[]> consumer;
    private long offsetInSchemasTopic;
    private Properties consumerProps;

    public KafkaStoreReaderThread(String bootstrapBrokers, String topic, String groupId, StoreUpdateHandler<K, V> storeUpdateHandler, Serializer<K> keySerializer, Serializer<V> valueSerializer, Store<K, V> localStore) {
