        this.keySerializer = keySerializer;
        this.valueSerializer = valueSerializer;
        this.localStore = localStore;
        this.offsetUpdateLock = new ReentrantLock();
        this.offsetReachedThreshold = offsetUpdateLock.newCondition();
        this.consumerProps = new Properties();
        this.consumerProps.put(ConsumerConfig.BOOTSTRAP_SERVERS_CONFIG, bootstrapBrokers);
        this.consumerProps.put(ConsumerConfig.GROUP_ID_CONFIG, groupId);
        this.consumerProps.put(ConsumerConfig.KEY_DESERIALIZER_CLASS_CONFIG, keySerializer.getClass().getName());
        this.consumerProps.put(ConsumerConfig.VALUE_DESERIALIZER_CLASS_CONFIG, valueSerializer.getClass().getName());
        this.consumerProps.put(ConsumerConfig.AUTO_OFFSET_RESET_CONFIG, "earliest");
        this.consumer = new KafkaConsumer<>(consumerProps);
        this.topicPartition = new TopicPartition(topic, 0);
        this.consumer.assign(Collections.singletonList(topicPartition));
        this.offsetInSchemasTopic = this.consumer.position(topicPartition);
    }

    public void doWork() {
        try {
            while (!Thread.currentThread().isInterrupted()) {
                for (ConsumerRecord<byte[], byte[]> record : consumer.poll(Duration.ofMillis(100))) {
                    K key = keySerializer.deserializeKey(record.key());
                    V value = valueSerializer.deserializeValue(record.value());
                    if (storeUpdateHandler.validate(key, value)) {
                        localStore.update(key, value);
                    }
                    offsetUpdateLock.lock();
                    try {
                        offsetInSchemasTopic = record.offset();
                        if (offsetInSchemasTopic >= 1000) {
                            offsetReachedThreshold.signalAll();
                        }
                    } finally {
                        offsetUpdateLock.unlock();
                    }
                }
            }
        } catch (Exception e) {
            e.printStackTrace();
        } finally {
            consumer.close();
        }
    }

    public void shutdown() {
        consumer.wakeup();
        Thread.currentThread().interrupt();
    }

    @Override
    public void run() {
        doWork();
    }
}
