import java.util.concurrent.BlockingQueue;
import java.util.concurrent.LinkedBlockingQueue;

public class NIOConnector implements Runnable {
    private String name;
    private Selector selector;
    private BlockingQueue<BackendConnection> connectQueue;
    private NIOProcessor[] processors;
    private int nextProcessor;
    private long connectCount;

    public NIOConnector(String name) {
        this.name = name;
        try {
            this.selector = Selector.open();
        } catch (Exception e) {
            e.printStackTrace();
        }
        this.connectQueue = new LinkedBlockingQueue<>();
        this.nextProcessor = 0;
        this.connectCount = 0;
    }

    public void setProcessors(NIOProcessor[] processors) {
        this.processors = processors;
    }

    public void postConnect(BackendConnection c) {
        connectQueue.add(c);
        selector.wakeup();
    }

    @Override
    public void run() {
        while (true) {
            connect();
            processEvents();
        }
    }

    private void connect() {
        while (!connectQueue.isEmpty()) {
            BackendConnection c = connectQueue.poll();
            if (c != null) {
                try {
                    c.connect();
                } catch (Exception e) {
                    e.printStackTrace();
                }
            }
        }
    }

    private void processEvents() {
        try {
            selector.select();
            for (SelectionKey key : selector.selectedKeys()) {
                if (key.isConnectable()) {
                    finishConnect(key, key.attachment());
                }
                clearSelectionKey(key);
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    private void finishConnect(SelectionKey key, Object att) {
        BackendConnection c = (BackendConnection) att;
        try {
            c.finishConnect(key);
            distributeConnection(c);
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    private void clearSelectionKey(SelectionKey key) {
        key.cancel();
    }

    private void distributeConnection(BackendConnection c) {
        NIOProcessor processor = processors[nextProcessor];
        processor.processConnection(c);
        nextProcessor = (nextProcessor + 1) % processors.length;
    }

    public NIOProcessor nextProcessor() {
        return processors[nextProcessor];
    }

    public static class ConnectIdGenerator {
        private static final Object lock = new Object();
        private static long counter = 0;

        public static long generateId() {
            synchronized (lock) {
                return ++counter;
            }
        }
    }
}
