    private static final String CMD_SHELL = "cmd.exe";

    public ProcessResult executeScript(TaskContext context, String scriptPath) {
        ProcessBuilder processBuilder = new ProcessBuilder();
        if (System.getProperty("os.name").toLowerCase().contains("win")) {
            processBuilder.command(CMD_SHELL, "/c", scriptPath);
        } else {
            processBuilder.command(SH_SHELL, "-c", scriptPath);
        }
        processBuilder.redirectErrorStream(true);
        Process process = null;
        try {
            process = processBuilder.start();
            StringBuilder output = new StringBuilder();
            StringBuilder error = new StringBuilder();
            copyStream(process.getInputStream(), output, context.getOmsLogger(), getCharset());
            copyStream(process.getErrorStream(), error, context.getOmsLogger(), getCharset());
            process.waitFor();
            return new ProcessResult(output.toString(), error.toString(), process.exitValue());
        } catch (Exception e) {
            return new ProcessResult("", "", -1);
        } finally {
            if (process != null) {
                process.destroy();
            }
        }
    }

    private void copyStream(InputStream is, StringBuilder sb, OmsLogger omsLogger, Charset charset) {
        try (BufferedReader reader = new BufferedReader(new InputStreamReader(is, charset))) {
            String line;
            while ((line = reader.readLine()) != null) {
                sb.append(line).append(System.lineSeparator());
                omsLogger.log(line);
            }
        } catch (Exception e) {
            omsLogger.log("Error reading stream: " + e.getMessage());
