    private static String buildCommand(Map<String, Object> parameters) {
        StringBuilder command = new StringBuilder();
        for (Map.Entry<String, Object> entry : parameters.entrySet()) {
            command.append(entry.getKey()).append(" ").append(entry.getValue()).append(" ");
        }
        return command.toString().trim();
    }

