        private Function<T, String> executeDir;

        public Job(Function<T, String> executeDir) {
            this.executeDir = executeDir;
        }

        public String executeDir() {
            return executeDir.apply(null);
        }
    }

    public static class HugeException extends Exception {
        public HugeException(String message) {
            super(message);
        }
    }
}
