        new BufferedInputStream(
            p.getInputStream(),
            100 * 1024);
    int c;
    while ((c = in.read()) != -1) {
      appOutput.write(c);
    }
    appOutput.flush();
    in.close();
  }
  p.waitFor();

  int status = p.exitValue();
  if (logger != null) {
    logger.info("exit status=" + status + " from " + pb.command());
  }
  return status;
