import java.io.InputStream;
import java.io.InputStreamReader;
import java.nio.file.Path;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;

public class ProcessExecutor {
    private ProcessBuilder mProcessBuilder;
    private Process mProcess;
    private ExecutorService mExecutor;

    public ProcessExecutor(Path scriptPath, Path libraryPath) {
        mProcessBuilder = new ProcessBuilder(scriptPath.toString());
        mProcessBuilder.directory(libraryPath.toFile());
    }

    public void execute(ProcessOutputHandler outputHandler) {
        mExecutor = Executors.newSingleThreadExecutor();
        mExecutor.submit(() -> {
            try {
                mProcess = mProcessBuilder.start();
                handleProcessOutput(mProcess.getInputStream(), outputHandler::handleOutput);
                int exitCode = mProcess.waitFor();
                outputHandler.handleCompletion(exitCode);
            } catch (IOException | InterruptedException e) {
                outputHandler.handleError(e.getMessage());
            }
        });
    }

    public void terminate() {
        if (mProcess != null) {
            mProcess.destroy();
        }
        if (mExecutor != null && !mExecutor.isShutdown()) {
            mExecutor.shutdownNow();
        }
    }

    private void handleProcessOutput(InputStream inputStream, ProcessOutputHandler outputHandler) {
        try (BufferedReader reader = new BufferedReader(new InputStreamReader(inputStream))) {
            String line;
            while ((line = reader.readLine()) != null) {
                outputHandler.handleOutput(line);
            }
        } catch (IOException e) {
            outputHandler.handleError(e.getMessage());
        }
