public class ProcessExecutor {
    private Process process;
    private ProcessBuilder processBuilder;

    public static Process execRedirected(String[] command) throws IOException {
        ProcessBuilder builder = new ProcessBuilder(command);
        builder.redirectErrorStream(true);
        return builder.start();
    }

    public int listenOnProcess(String[] arguments, ProcessOutputHandler handler) {
        try {
            ProcessBuilder builder = new ProcessBuilder(arguments);
            process = builder.start();
            new Thread(new StreamGobbler(process.getInputStream(), handler::handleOutputLine)).start();
            new Thread(new StreamGobbler(process.getErrorStream(), handler::handleError)).start();
            return process.waitFor();
        } catch (IOException | InterruptedException e) {
            return -1;
        }
    }

    public interface ProcessOutputHandler {
        void handleOutputLine(String line);
        void handleError(String errorMessage);
    }

    private static class StreamGobbler implements Runnable {
        private InputStream inputStream;
        private ProcessOutputHandler handler;

        public StreamGobbler(InputStream inputStream, ProcessOutputHandler handler) {
            this.inputStream = inputStream;
            this.handler = handler;
        }

        @Override
        public void run() {
            try (BufferedReader reader = new BufferedReader(new InputStreamReader(inputStream))) {
                String line;
                while ((line = reader.readLine()) != null) {
                    handler.handleOutputLine(line);
                }
            } catch (IOException e) {
                handler.handleError(e.getMessage());
            }
        }
    }
