public class ExtCmd {
    private Map<ExtCmdCondition, String> conditionsWithDescriptions;
    private Set<ExtCmdEndedListener> extCmdEndedListenerSet;
    private OutputReceiver outReceiver;
    private OutputReceiver errReceiver;
    private ErrorReporter errLog;
    private long startTime;
    private String[] execCommand;
    private String execCommandStr;
    private boolean logExecution;

    public ExtCmd() {
        conditionsWithDescriptions = new HashMap<>();
        extCmdEndedListenerSet = new HashSet<>();
        outReceiver = new OutputReceiver();
        errReceiver = new OutputReceiver();
        errLog = new ErrorReporter();
        startTime = 0;
        logExecution = false;
    }

    public OutputStream exec(ProcessBuilder.Redirect redirect, File dir, String... args) throws IOException {
        execCommand = args;
        execCommandStr = Arrays.toString(args);
        startTime = System.currentTimeMillis();
        if (logExecution) {
            errLog.log("Executing command: " + execCommandStr);
        }
        ProcessBuilder processBuilder = new ProcessBuilder(args);
        processBuilder.redirectOutput(redirect);
        processBuilder.redirectError(redirect);
        processBuilder.directory(dir);
        Process process = processBuilder.start();
        return process.getOutputStream();
    }

    public OutputData syncProcess() throws InterruptedException {
        Process process = null;
        try {
            process = new ProcessBuilder(execCommand).start();
            process.waitFor();
            List<String> stdout = outReceiver.getOutput(process.getInputStream());
            List<String> stderr = errReceiver.getOutput(process.getErrorStream());
            int exitCode = process.exitValue();
            notifyListeners(stdout, stderr, exitCode);
            return new OutputData(stdout, stderr, exitCode);
        } catch (IOException | InterruptedException e) {
            if (process != null) {
                process.destroy();
            }
            throw e;
        } finally {
            if (process != null) {
                process.destroy();
            }
        }
    }

    private void checkForConditions() {
        for (Map.Entry<ExtCmdCondition, String> entry : conditionsWithDescriptions.entrySet()) {
            if (!entry.getKey().isMet()) {
                errLog.log("Condition not met: " + entry.getValue());
                throw new IllegalStateException("Condition not met: " + entry.getValue());
            }
        }
    }

    public void addCondition(ExtCmdCondition condition, String description) {
        conditionsWithDescriptions.put(condition, description);
    }

    public void addListener(ExtCmdEndedListener listener) {
        extCmdEndedListenerSet.add(listener);
    }

    private void notifyListeners(List<String> stdout, List<String> stderr, int exitCode) {
        for (ExtCmdEndedListener listener : extCmdEndedListenerSet) {
            listener.onEnded(stdout, stderr, exitCode);
        }
    }

    public static class OutputData {
        private List<String> stdout;
        private List<String> stderr;
        private int exitCode;

        public OutputData(List<String> stdout, List<String> stderr, int exitCode) {
            this.stdout = stdout;
            this.stderr = stderr;
            this.exitCode = exitCode;
        }

        public List<String> getStdout() {
            return stdout;
        }

        public List<String> getStderr() {
            return stderr;
        }

        public int getExitCode() {
            return exitCode;
        }
    }

    public interface ExtCmdCondition {
        boolean isMet();
    }

    public interface ExtCmdEndedListener {
        void onEnded(List<String> stdout, List<String> stderr, int exitCode);
    }

    public static class OutputReceiver {
        private List<String> output;

        public OutputReceiver() {
            output = new ArrayList<>();
        }

        public List<String> getOutput(InputStream inputStream) throws IOException {
