public class RedisPublisher {
    private RedisClient client;
    private StatefulRedisConnection<String, String> connection;

    public RedisPublisher() {
        client = RedisClient.create("redis://localhost:6379");
        connection = client.connect();
        RedisListCommands<String, String> listCommands = connection.sync();
        listCommands.flushall();
    }

    public Publisher<String> createPublisher(long elements) {
        if (elements == Long.MAX_VALUE) {
            return null;
        }
        RedisListCommands<String, String> listCommands = connection.sync();
        for (long i = 0; i < elements; i++) {
            listCommands.rpush("mylist", "element" + i);
        }
        return Flux.generate(sink -> {
            RedisListCommands<String, String> listCommandsSync = connection.sync();
            List<String> elements = listCommandsSync.lrange("mylist", 0, -1);
            for (String element : elements) {
                sink.next(element);
            }
            sink.complete();
        });
    }

    public void close() {
        connection.close();
        client.shutdown();
    }
}
