import redis.clients.jedis.JedisPool;
import redis.clients.jedis.JedisPoolConfig;
import redis.clients.jedis.Jedis;
import redis.clients.jedis.exceptions.JedisConnectionException;
import java.util.concurrent.ConcurrentHashMap;
import java.util.concurrent.ConcurrentMap;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;
import java.util.function.Consumer;

public class RedisRegistry {
    private ConcurrentMap<String, JedisPool> jedisPools;
    private String clusterName;
    private boolean replicate;
    private ConcurrentMap<String, Notifier> notifiers;

    public RedisRegistry(AppContext appContext) {
        this.clusterName = appContext.getClusterName();
        this.replicate = appContext.isReplicate();
        this.jedisPools = new ConcurrentHashMap<>();
        this.notifiers = new ConcurrentHashMap<>();
        initializePools(appContext.getRedisServers());
    }

    protected void doRegister(Node node) throws NodeRegistryException {
        try (Jedis jedis = jedisPools.get(node.getRedisServer()).getResource()) {
            jedis.set(node.getKey(), node.getValue());
            if (replicate) {
                replicateToAllServers(node);
            }
        } catch (JedisConnectionException e) {
            throw new NodeRegistryException("Failed to register node", e);
        }
    }

    protected void doSubscribe(Node node, NotifyListener listener) {
        Notifier notifier = new Notifier(node, listener);
        notifiers.put(node.getKey(), notifier);
        notifier.start();
    }

    private void initializePools(List<String> redisServers) {
        for (String server : redisServers) {
            JedisPoolConfig poolConfig = new JedisPoolConfig();
            jedisPools.put(server, new JedisPool(poolConfig, server));
        }
    }

    private void replicateToAllServers(Node node) {
        for (JedisPool pool : jedisPools.values()) {
            try (Jedis jedis = pool.getResource()) {
                jedis.set(node.getKey(), node.getValue());
            }
        }
    }

    private class NotifySub extends JedisPubSub {
        @Override
        public void onMessage(String key, String msg) {
            Notifier notifier = notifiers.get(key);
            if (notifier != null) {
                notifier.notify(msg);
            }
        }
    }

    private class Notifier extends Thread {
        private Node node;
        private NotifyListener listener;
        private volatile boolean running = true;

        public Notifier(Node node, NotifyListener listener) {
            this.node = node;
            this.listener = listener;
        }

        @Override
        public void run() {
            try (Jedis jedis = jedisPools.get(node.getRedisServer()).getResource()) {
                jedis.subscribe(new NotifySub(), node.getKey());
            } catch (JedisConnectionException e) {
                running = false;
            }
        }

        public void shutdown() {
            running = false;
            interrupt();
        }

        private void notify(String msg) {
            if (running) {
                listener.onNotification(node.getKey(), msg);
            }
        }
    }
}
