public class RedisPooledClient {
    private JedisPooled jedisPool;

    public RedisPooledClient(String host, int port, String password) {
        JedisPoolConfig poolConfig = new JedisPoolConfig();
        jedisPool = new JedisPooled(host, port, poolConfig);
        if (password != null && !password.isEmpty()) {
            jedisPool.getResource().auth(password);
        }
    }

    public void performOperations(int totalOperations) {
        int threadCount = Runtime.getRuntime().availableProcessors();
        ExecutorService executorService = Executors.newFixedThreadPool(threadCount);
        for (int i = 0; i < threadCount; i++) {
            executorService.submit(() -> {
                for (int j = 0; j < totalOperations / threadCount; j++) {
                    String key = "key" + Thread.currentThread().getId() + j;
                    String value = "value" + Thread.currentThread().getId() + j;
                    jedisPool.getResource().set(key, value);
                    String result = jedisPool.getResource().get(key);
                    assert result.equals(value);
                }
            });
        }
        executorService.shutdown();
        try {
            executorService.awaitTermination(Long.MAX_VALUE, TimeUnit.NANOSECONDS);
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
        }
    }

    public void close() {
        if (jedisPool != null) {
            jedisPool.close();
        }
    }
