import redis.clients.jedis.Jedis;
import redis.clients.jedis.JedisPool;
import redis.clients.jedis.JedisPoolConfig;
import redis.clients.jedis.exceptions.JedisConnectionException;

import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;
import java.util.concurrent.TimeUnit;

public class RedisConnectionPool {
    private JedisPool pool;
    private EndpointConfig endpoint;

    public RedisConnectionPool(EndpointConfig endpoint) {
        this.endpoint = endpoint;
        JedisPoolConfig poolConfig = new JedisPoolConfig();
        poolConfig.setMaxTotal(10);
        poolConfig.setMaxIdle(5);
        poolConfig.setMinIdle(1);
        poolConfig.setMaxWaitMillis(1000);
        poolConfig.setTestOnBorrow(true);
        poolConfig.setTestOnReturn(true);
        poolConfig.setTestWhileIdle(true);
        poolConfig.setMinEvictableIdleTimeMillis(300000);
        poolConfig.setNumTestsPerEvictionRun(3);
        poolConfig.setEvictionPolicy(JedisPoolConfig.EvictionPolicy.LIFO);
        this.pool = new JedisPool(poolConfig, endpoint.getHost(), endpoint.getPort(), endpoint.getPassword());
    }

    public void executeOperations(int totalOperations) {
        ExecutorService executor = Executors.newFixedThreadPool(50);
        for (int i = 0; i < totalOperations; i++) {
            executor.submit(this::performOperation);
        }
        executor.shutdown();
        try {
            if (!executor.awaitTermination(1, TimeUnit.MINUTES)) {
                executor.shutdownNow();
            }
        } catch (InterruptedException e) {
            executor.shutdownNow();
            Thread.currentThread().interrupt();
        }
    }

    private void performOperation() {
        try (Jedis jedis = pool.getResource()) {
            String key = "key" + Math.random();
            String value = "value" + Math.random();
            jedis.set(key, value);
            String result = jedis.get(key);
            if (result != null) {
                System.out.println("Operation successful: " + result);
            } else {
                System.out.println("Operation failed");
            }
        } catch (JedisConnectionException e) {
            System.out.println("Redis connection error: " + e.getMessage());
        }
    }

    public void close() {
        if (pool != null) {
            pool.destroy();
        }
    }
}
