import redis.clients.jedis.Jedis;
import redis.clients.jedis.JedisFactory;
import redis.clients.jedis.JedisClientConfig;
import redis.clients.util.Pool;

import java.util.Collection;
import java.util.HashSet;
import java.util.List;
import java.util.Set;
import java.util.concurrent.locks.Lock;
import java.util.concurrent.atomic.AtomicBoolean;

public class JedisSentinelPool implements Pool<Jedis> {
    private static final Logger LOG = LoggerFactory.getLogger(JedisSentinelPool.class);
    private final JedisFactory factory;
    private final JedisClientConfig sentinelClientConfig;
    private final Collection<MasterListener> masterListeners;
    private volatile HostAndPort currentHostMaster;
    private final Lock initPoolLock = new ReentrantLock();

    public JedisSentinelPool(String masterName, Set<HostAndPort> sentinels, JedisFactory factory, JedisClientConfig sentinelClientConfig) {
        this.factory = factory;
        this.sentinelClientConfig = sentinelClientConfig;
        this.masterListeners = new HashSet<>();
        initPool(masterName, sentinels);
    }

    private void initPool(String masterName, Set<HostAndPort> sentinels) {
        initPoolLock.lock();
        try {
            currentHostMaster = initSentinels(sentinels, masterName);
            masterListeners.forEach(MasterListener::shutdown);
            masterListeners.clear();
            masterListeners.add(new MasterListener(masterName, currentHostMaster.getHost(), currentHostMaster.getPort()));
        } finally {
            initPoolLock.unlock();
        }
    }

    private HostAndPort initSentinels(Set<HostAndPort> sentinels, String masterName) {
        HostAndPort master = null;
        for (HostAndPort sentinel : sentinels) {
            Jedis jedis = factory.createResource();
            try {
                Set<String> masterNames = jedis.sentinelGetMasterNames();
                if (masterNames.contains(masterName)) {
                    master = jedis.sentinelGetMasterAddrByName(masterName);
                    break;
                }
            } finally {
                jedis.close();
            }
        }
        if (master == null) {
            throw new IllegalStateException("Master not found for name: " + masterName);
        }
        return master;
    }

    private HostAndPort toHostAndPort(List<String> sentinelResponse) {
        String host = sentinelResponse.get(0);
        String portStr = sentinelResponse.get(1);
        int port = Integer.parseInt(portStr);
        return new HostAndPort(host, port);
    }

    @Override
    public Jedis getResource() {
        synchronized (this) {
            if (currentHostMaster == null) {
                throw new IllegalStateException("Pool not initialized");
            }
            Jedis jedis = factory.createResource();
            jedis.connect(currentHostMaster.getHost(), currentHostMaster.getPort());
            return jedis;
        }
    }

    @Override
    public void returnResource(Jedis resource) {
        resource.close();
    }

    private class MasterListener implements Runnable {
        private final String masterName;
        private final String host;
        private final int port;
        private final long subscribeRetryWaitTimeMillis;
        private final Jedis j;
        private final AtomicBoolean running = new AtomicBoolean(true);

        public MasterListener(String masterName, String host, int port) {
            this.masterName = masterName;
            this.host = host;
            this.port = port;
            this.subscribeRetryWaitTimeMillis = 1000;
            this.j = factory.createResource();
            j.connect(host, port);
        }

        @Override
        public void run() {
            try {
                while (running.get()) {
                    try {
                        j.subscribe(new JedisPubSub() {
                            @Override
                            public void onMessage(String channel, String message) {
                                if ("message".equals(channel)) {
                                    String[] parts = message.split(" ");
                                    if ("switch-master".equals(parts[0])) {
                                        String newMasterName = parts[1];
                                        String newMasterAddr = parts[2] + " " + parts[3];
                                        HostAndPort newMaster = toHostAndPort(Arrays.asList(newMasterAddr.split(" ")));
                                        synchronized (JedisSentinelPool.this) {
                                            currentHostMaster = newMaster;
                                            masterListeners.forEach(masterListener -> {
                                                masterListener.shutdown();
                                                masterListeners.remove(masterListener);
                                            });
                                            masterListeners.add(new MasterListener(masterName, newMaster.getHost(), newMaster.getPort()));
                                        }
                                    }
                                }
                            }
                        }, "message");
                        Thread.sleep(subscribeRetryWaitTimeMillis);
                    } catch (Exception e) {
                        LOG.error("Error in MasterListener", e);
                        Thread.sleep(subscribeRetryWaitTimeMillis);
                    }
                }
            } finally {
                j.close();
            }
        }

        public void shutdown() {
            running.set(false);
        }
    }
}
