import redis.clients.jedis.Jedis;
import redis.clients.jedis.JedisClientConfig;
import redis.clients.jedis.JedisPool;
import redis.clients.jedis.JedisPoolConfig;
import java.util.Set;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;

public class SentineledConnectionProvider {
    private HostAndPort currentMaster;
    private JedisPool pool;
    private String masterName;
    private JedisClientConfig masterClientConfig;
    private JedisClientConfig sentinelClientConfig;

    public SentineledConnectionProvider(String masterName, JedisClientConfig masterClientConfig, Set<HostAndPort> sentinels, JedisClientConfig sentinelClientConfig) {
        this.masterName = masterName;
        this.masterClientConfig = masterClientConfig;
        this.sentinelClientConfig = sentinelClientConfig;
        initMaster(initSentinels(sentinels));
    }

    public Jedis getConnection() {
        return pool.getResource();
    }

    public void initMaster(HostAndPort master) {
        if (pool != null) {
            pool.close();
        }
        JedisPoolConfig poolConfig = new JedisPoolConfig();
        poolConfig.setMaxTotal(10);
        poolConfig.setMaxIdle(5);
        poolConfig.setMinIdle(1);
        poolConfig.setMaxWaitMillis(1000);
        poolConfig.setTestOnBorrow(true);
        poolConfig.setTestOnReturn(true);
        poolConfig.setTestWhileIdle(true);
        poolConfig.setMinEvictableIdleTimeMillis(300000);
        poolConfig.setNumTestsPerEvictionRun(3);
        poolConfig.setTimeBetweenEvictionRunsMillis(60000);
        pool = new JedisPool(poolConfig, master.getHost(), master.getPort(), 10000, masterClientConfig);
    }

    public HostAndPort initSentinels(Set<HostAndPort> sentinels) {
        Jedis jedis = new Jedis(sentinels.iterator().next().getHost(), sentinels.iterator().next().getPort());
        jedis.connect();
        String master = jedis.sentinelGetMasterName(masterName);
        jedis.close();
        return HostAndPort.parse(master);
    }

    public class SentinelListener extends Thread {
        private HostAndPort node;

        public SentinelListener(HostAndPort node) {
            this.node = node;
        }

        @Override
        public void run() {
            Jedis jedis = new Jedis(node.getHost(), node.getPort());
            jedis.connect();
            while (!Thread.currentThread().isInterrupted()) {
                try {
                    String master = jedis.sentinelGetMasterName(masterName);
                    HostAndPort masterNode = HostAndPort.parse(master);
                    if (!masterNode.equals(currentMaster)) {
                        initMaster(masterNode);
                        currentMaster = masterNode;
                    }
                } catch (Exception e) {
                    e.printStackTrace();
                }
                try {
                    Thread.sleep(5000);
                } catch (InterruptedException e) {
                    Thread.currentThread().interrupt();
                }
            }
            jedis.close();
        }

        public void shutdown() {
            interrupt();
        }
    }
}
