import java.io.IOException;
import java.nio.MappedByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;
import java.util.Arrays;

public class ArrayLongLongMapMmap {
    private Path path;
    private FileChannel writeChannel;
    private MappedByteBuffer[] segmentsArray;
    private FileChannel readChannel;

    public ArrayLongLongMapMmap(Path path, boolean madvise) throws IOException {
        this.path = path;
        this.writeChannel = FileChannel.open(path, StandardOpenOption.CREATE, StandardOpenOption.WRITE);
    }

    public void init() throws IOException {
        long fileSize = writeChannel.size();
        long segmentSize = 1L << 27; // 128MB
        int numSegments = (int) (fileSize / segmentSize);
        if (fileSize % segmentSize != 0) {
            numSegments++;
        }
        segmentsArray = new MappedByteBuffer[numSegments];
        for (int i = 0; i < numSegments; i++) {
            long position = i * segmentSize;
            segmentsArray[i] = writeChannel.map(FileChannel.MapMode.READ_WRITE, position, segmentSize);
        }
        writeChannel.close();
        readChannel = FileChannel.open(path, StandardOpenOption.READ);
    }

    public long get(long key) {
        long segmentIndex = key >>> 27;
        long segmentPosition = key & ((1L << 27) - 1);
        MappedByteBuffer buffer = segmentsArray[segmentIndex];
        return buffer.getLong(segmentPosition);
    }

    public void close() throws IOException {
        if (segmentsArray != null) {
            Arrays.stream(segmentsArray).forEach(MappedByteBuffer::force);
        }
        if (readChannel != null) {
            readChannel.close();
        }
        if (writeChannel != null) {
            writeChannel.close();
        }
        Path tempPath = path.resolveSibling(path.getFileName().toString() + ".tmp");
        if (tempPath.toFile().delete()) {
            path.toFile().delete();
        }
    }
}
