import org.apache.flink.streaming.api.functions.sink.RichSinkFunction;
import org.apache.rocketmq.client.producer.DefaultMQProducer;
import org.apache.rocketmq.client.producer.Message;
import org.apache.rocketmq.client.producer.SendResult;
import org.apache.rocketmq.common.message.MessageQueue;
import org.apache.flink.api.common.serialization.KeyValueSerializationSchema;
import org.apache.flink.configuration.Configuration;
import org.apache.flink.api.common.functions.TopicSelector;
import org.apache.flink.api.common.serialization.DeserializationSchema;
import org.apache.flink.api.common.serialization.SerializationSchema;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import java.util.List;
import java.util.Properties;
import java.util.concurrent.ConcurrentLinkedQueue;

public class RocketMQSink<IN> extends RichSinkFunction<IN> {
    private static final long serialVersionUID = 1L;
    private final Logger LOG = LoggerFactory.getLogger(RocketMQSink.class);
    private DefaultMQProducer producer;
    private boolean async;
    private Properties props;
    private TopicSelector<IN> topicSelector;
    private KeyValueSerializationSchema<IN> serializationSchema;
    private boolean batchFlushOnCheckpoint;
    private int batchSize;
    private List<Message> batchList;
    private int messageDeliveryDelayLevel;

    public RocketMQSink(KeyValueSerializationSchema<IN> serializationSchema,
                        TopicSelector<IN> topicSelector,
                        Properties props) {
        this.serializationSchema = serializationSchema;
        this.topicSelector = topicSelector;
        this.props = props;
    }

    @Override
    public void open(Configuration parameters) throws Exception {
        super.open(parameters);
        producer = new DefaultMQProducer(props.getProperty("producerGroup"));
        producer.setNamesrvAddr(props.getProperty("namesrvAddr"));
        producer.start();
        this.async = Boolean.parseBoolean(props.getProperty("async"));
        this.batchFlushOnCheckpoint = Boolean.parseBoolean(props.getProperty("batchFlushOnCheckpoint"));
        this.batchSize = Integer.parseInt(props.getProperty("batchSize"));
        this.batchList = new ConcurrentLinkedQueue<>();
        this.messageDeliveryDelayLevel = Integer.parseInt(props.getProperty("messageDeliveryDelayLevel"));
    }

    @Override
    public void invoke(IN value, Context context) throws Exception {
        MessageQueue mq = topicSelector.select(value, this.getRuntimeContext().getIndexOfThisSubtask());
        Message message = prepareMessage(value);
        if (async) {
            producer.sendOneway(message);
        } else {
            SendResult sendResult = producer.send(message);
            LOG.info("Send message result: " + sendResult);
        }
        if (batchFlushOnCheckpoint) {
            batchList.add(message);
            if (batchList.size() >= batchSize) {
                flushSync();
            }
        }
    }

    private Message prepareMessage(IN value) throws Exception {
        String key = serializationSchema.serializeKey(value);
        String valueStr = serializationSchema.serializeValue(value);
        Message message = new Message(topicSelector.select(value, this.getRuntimeContext().getIndexOfThisSubtask()),
                key, valueStr.getBytes());
        message.setDelayTimeLevel(messageDeliveryDelayLevel);
        return message;
    }

    @Override
    public void flush() throws Exception {
        if (batchFlushOnCheckpoint) {
            flushSync();
        }
    }

    private void flushSync() throws Exception {
        if (!batchList.isEmpty()) {
            producer.sendMessages(batchList);
            batchList.clear();
        }
    }

    @Override
    public void close() throws Exception {
        if (producer != null) {
            producer.shutdown();
        }
    }
}
