import java.io.*;
import java.net.*;
import javax.servlet.*;
import javax.servlet.http.*;

public class Lisplet extends HttpServlet {
    private String host;
    private int port;

    public void doPost(HttpServletRequest req, HttpServletResponse resp) throws ServletException, IOException {
        PrintWriter out = resp.getWriter();
        try {
            Socket socket = new Socket(host, port);
            OutputStream os = socket.getOutputStream();
            Writer writer = new OutputStreamWriter(os);
            sendRequest(req, writer);
            writer.flush();

            InputStream is = socket.getInputStream();
            PushbackInputStream strm = new PushbackInputStream(is);
            if (!getResponseHeader(req, resp, strm)) {
                return;
            }
            transferResponseBody(resp, strm);
        } catch (UnknownHostException e) {
            out.println("Server not found: " + e.getMessage());
        } catch (IOException e) {
            out.println("I/O error: " + e.getMessage());
        } finally {
            out.close();
        }
    }

    private void transferResponseBody(HttpServletResponse resp, PushbackInputStream strm) throws IOException {
        PrintWriter out = resp.getWriter();
        int b;
        while ((b = strm.read()) != -1) {
            out.write(b);
        }
    }

    private boolean getResponseHeader(HttpServletRequest req, HttpServletResponse resp, PushbackInputStream strm) throws IOException {
        BufferedReader reader = new BufferedReader(new InputStreamReader(strm));
        String line;
        while ((line = reader.readLine()) != null && !line.isEmpty()) {
            if (line.startsWith("HTTP/")) {
                resp.setStatus(HttpServletResponse.SC_OK);
                return true;
            }
        }
        return false;
    }

    private void sendRequest(HttpServletRequest req, Writer writer) throws IOException {
        writer.write("POST / HTTP/1.1\r\n");
        writer.write("Host: " + req.getServerName() + ":" + req.getServerPort() + "\r\n");
        writer.write("Content-Type: application/x-www-form-urlencoded\r\n");
        writer.write("Content-Length: " + req.getContentLength() + "\r\n");
        writer.write("\r\n");
        writer.write(req.getContentType() + "=" + req.getContentEncoding() + "\r\n");
        writer.write(req.getParameterMap().toString());
    }

    private void writeTagValuePair(Writer writer, String tag, String value) throws IOException {
        writer.write(tag + "=" + value + "\r\n");
    }

    public void init() throws ServletException {
        host = getServletConfig().getInitParameter("host");
        port = Integer.parseInt(getServletConfig().getInitParameter("port"));
    }
}
