        writer.flush();
        ZipInputStream zis = new ZipInputStream(reader.getInputStream());
        BufferedImage image = ImageIO.read(zis);
        zis.close();
        return image;
    }

    public void complete() throws IOException {
        writer.println("complete");
        writer.flush();
        String response = reader.nextLine();
        if (!response.equals("OK")) {
            throw new IOException("Server operation not completed successfully");
        }
    }

    private class Reader extends BufferedReader {
        public Reader(InputStream in) {
            super(new InputStreamReader(in));
        }

        public String nextLine() throws IOException {
            return super.readLine();
        }
    }

    private class Writer extends BufferedWriter {
        public Writer(OutputStream out) {
            super(new OutputStreamWriter(out));
        }

