import java.io.IOException;
import java.io.InputStream;
import java.io.InputStreamReader;
import java.net.Socket;

public class TProfilerClient {
    private Socket socket;
    private DataOutputStream out;
    private BufferedReader in;

    public void start(String server, int port) throws IOException {
        doSend("START", server, port);
    }

    public void stop(String server, int port) throws IOException {
        doSend("STOP", server, port);
    }

    public String status(String server, int port) throws IOException {
        return getStatus("STATUS", server, port);
    }

    public void flushMethod(String server, int port) throws IOException {
        doSend("FLUSHMETHOD", server, port);
    }

    private void doSend(String command, String server, int port) throws IOException {
        socket = new Socket(server, port);
        out = new DataOutputStream(socket.getOutputStream());
        out.writeBytes(command + "\n");
        out.flush();
        socket.close();
    }

    private String getStatus(String command, String server, int port) throws IOException {
        socket = new Socket(server, port);
        out = new DataOutputStream(socket.getOutputStream());
        in = new BufferedReader(new InputStreamReader(socket.getInputStream()));
        out.writeBytes(command + "\n");
        out.flush();
        String response = read(in);
        socket.close();
        return response;
    }

    private String read(InputStream in) throws IOException {
        StringBuilder sb = new StringBuilder();
        int i;
        while ((i = in.read()) != '\n') {
            sb.append((char) i);
        }
        return sb.toString();
    }
}
