import java.net.InetSocketAddress;
import java.nio.channels.SelectionKey;
import java.nio.channels.SocketChannel;
import java.util.Map;
import java.util.Set;
import java.util.concurrent.TimeUnit;
import javax.net.ssl.SSLFactory;

public class NioTcpClient {
    private Selector nioSelector;
    private Map<String, SelectionKey> keyMap;
    private Time time;
    private SSLFactory sslFactory;
    private NetworkConfig networkConfig;

    public NioTcpClient(Time time, SSLFactory sslFactory, NetworkConfig networkConfig) {
        this.nioSelector = Selector.open();
        this.time = time;
        this.sslFactory = sslFactory;
        this.networkConfig = networkConfig;
        this.keyMap = new HashMap<>();
    }

    public String connect(InetSocketAddress address, int readBufferSize, int writeBufferSize, PortType portType) throws IOException {
        SocketChannel channel = SocketChannel.open();
        channel.configureBlocking(false);
        channel.connect(address);
        SelectionKey key = channel.register(nioSelector, SelectionKey.OP_CONNECT);
        keyMap.put(generateConnectionId(channel), key);
        return generateConnectionId(channel);
    }

    private String generateConnectionId(SocketChannel channel) {
        String localAddress = channel.socket().getLocalAddress().toString();
        String remoteAddress = channel.socket().getRemoteAddress().toString();
        return localAddress + "-" + remoteAddress;
    }

    private Transmission createTransmission(String data, SelectionKey key, String connectionId, int bufferSize, PortType portType, SSLFactory.Mode mode) {
        if (portType == PortType.SSL) {
            return new SslTransmission(data, key, sslFactory, mode);
        } else {
            return new PlainTextTransmission(data, key, bufferSize);
        }
    }

    public void disconnect(String connectionId) {
        SelectionKey key = keyMap.remove(connectionId);
        if (key != null) {
            key.cancel();
        }
    }

    public void close() throws IOException {
        nioSelector.close();
        for (SelectionKey key : keyMap.values()) {
            key.channel().close();
        }
    }
}
