public class AsynchronousSocketChannelStream {
    private final String serverAddress;
    private final InetAddressResolver inetAddressResolver;
    private final SocketSettings settings;
    private final AsynchronousChannelGroup group;

    public AsynchronousSocketChannelStream(String serverAddress, InetAddressResolver inetAddressResolver, SocketSettings settings, AsynchronousChannelGroup group) {
        this.serverAddress = serverAddress;
        this.inetAddressResolver = inetAddressResolver;
        this.settings = settings;
        this.group = group;
    }

    public void openAsync(OperationContext operationContext, AsyncCompletionHandler<Void> handler) {
        InetAddressResolver.Result result = inetAddressResolver.resolve(serverAddress);
        if (result.isSuccess()) {
            InetSocketAddress address = new InetSocketAddress(result.getInetAddress(), settings.getPort());
            AsynchronousSocketChannel channel = AsynchronousSocketChannel.open(group);
            channel.connect(address, null, new OpenCompletionHandler<>(handler, result.getSocketAddressQueue(), channel));
        } else {
            handler.completed(null, operationContext);
        }
    }

    private void initializeSocketChannel(AsyncCompletionHandler<Void> handler, Queue<SocketAddress> socketAddressQueue) {
        if (!socketAddressQueue.isEmpty()) {
            AsynchronousSocketChannel attemptConnectionChannel = AsynchronousSocketChannel.open(group);
            InetSocketAddress address = (InetSocketAddress) socketAddressQueue.poll();
            attemptConnectionChannel.connect(address, null, new OpenCompletionHandler<>(handler, socketAddressQueue, attemptConnectionChannel));
        } else {
            handler.failed(new Exception("No more addresses to try"), null);
        }
    }

    private static class OpenCompletionHandler<T> implements CompletionHandler<Void, Queue<SocketAddress>> {
        private final AsyncCompletionHandler<Void> handler;
        private final Queue<SocketAddress> socketAddressQueue;
        private final AsynchronousSocketChannel attemptConnectionChannel;

        public OpenCompletionHandler(AsyncCompletionHandler<Void> handler, Queue<SocketAddress> socketAddressQueue, AsynchronousSocketChannel attemptConnectionChannel) {
            this.handler = handler;
            this.socketAddressQueue = socketAddressQueue;
            this.attemptConnectionChannel = attemptConnectionChannel;
        }

        @Override
        public void completed(Void result, Queue<SocketAddress> attachment) {
            handler.completed(result, null);
        }

        @Override
        public void failed(Throwable exc, Queue<SocketAddress> attachment) {
            if (attachment.isEmpty()) {
                handler.failed(exc, null);
            } else {
                initializeSocketChannel(handler, attachment);
            }
        }
    }

