public class KafkaAdminTopicConfigProvider {
    private final Properties _clusterConfigs;
    private final AdminClient _adminClient;
    private static final Duration DESCRIBE_CLUSTER_CONFIGS_TIMEOUT = Duration.ofSeconds(90);
    private static final Logger LOG = Logger.getLogger(KafkaAdminTopicConfigProvider.class.getName());

    public KafkaAdminTopicConfigProvider(Properties clusterConfigs) {
        _clusterConfigs = clusterConfigs;
        _adminClient = AdminClient.create(clusterConfigs);
    }

    public Properties topicConfigs(String topic) {
        try {
            ConfigResource configResource = new ConfigResource(Type.TOPIC, topic);
            DescribeConfigsResult result = _adminClient.describeConfigs(Collections.singletonList(configResource));
            Map<ConfigResource, Config> configMap = result.values().get();
            Config config = configMap.get(configResource);
            return convertConfigToProperties(config);
        } catch (AdminClientException | TimeoutException e) {
            LOG.warning("Failed to retrieve configuration for topic: " + topic);
            return new Properties();
        }
    }

    public Map<String, Properties> topicConfigs(Set<String> topics) {
        try {
            List<ConfigResource> configResources = topics.stream()
                    .map(topic -> new ConfigResource(Type.TOPIC, topic))
                    .collect(Collectors.toList());
            DescribeConfigsResult result = _adminClient.describeConfigs(configResources);
            Map<ConfigResource, Config> configMap = result.values().get();
            return configMap.entrySet().stream()
                    .collect(Collectors.toMap(
                            entry -> entry.getKey().name(),
                            entry -> convertConfigToProperties(entry.getValue())
                    ));
        } catch (AdminClientException | TimeoutException e) {
            LOG.warning("Failed to retrieve configurations for topics: " + topics);
            return new HashMap<>();
        }
    }

    public Map<String, Properties> allTopicConfigs() {
        try {
            DescribeTopicsResult result = _adminClient.describeTopics(_adminClient.listTopics().names().get());
            Map<String, TopicDescription> topicDescriptions = result.values().get();
            return topicDescriptions.entrySet().stream()
                    .collect(Collectors.toMap(
                            Map.Entry::getKey,
                            entry -> convertConfigToProperties(entry.getValue().config())
                    ));
        } catch (AdminClientException | TimeoutException e) {
            LOG.warning("Failed to retrieve configurations for all topics");
            return new HashMap<>();
        }
    }

    private Properties convertConfigToProperties(Config config) {
        Properties properties = new Properties();
        if (config != null) {
            for (Map.Entry<String, String> entry : config.entries().entrySet()) {
                if (entry.getValue() != null) {
                    properties.setProperty(entry.getKey(), entry.getValue());
                }
            }
        }
        return properties;
    }
}
